var should                = require('chai').should(),
    qte  = require('../index');

describe('#qte', function() {
  it('it converts quaternion angles into euler angles', function() {
    var quaternion = [
      -0.9950784503940929,
      -0.004831829422759175,
      -0.000611604384887619,
       0.09897048512458934
    ];

    var euler = qte(quaternion);

    euler[0].should.be.closeTo(0.009495, 0.000001);
    euler[1].should.be.closeTo(0.002174, 0.000001);
    euler[2].should.be.closeTo(-0.198258, 0.000001);
  });
});